import React, {useState} from "react";
import {Panel, Div, Title, Text, Link} from "@vkontakte/vkui";
import './css/myThingsPage.css';

const myThingsPage = () =>{

    let state1P = true;
    let state2P = true;
    let state3P = true;
    
    function ChangeColorBtn(classNameNumber, stateNumber){

        var className = `.stateEm${classNameNumber}`

        if(stateNumber){
            document.querySelector(className).style.background = "#ffffff";
            document.querySelector(className).style.color = "#005020";
            stateNumber=false;
        }
        else{
            document.querySelector(className).style.background = "#3C5F4C";
            document.querySelector(className).style.color = "#ffffff";
            stateNumber=true;
        }  
    }

    return(
        <Panel id="home"> 
            <Div className="mainThings">
                <Div className="vetVerx"></Div>
                <Div className="myThings">Мои мысли...</Div>
                <Div className="discriptionThing">
                    <Text>Сегодня не случилось ничего хорошего и ничего плохого. Иногда мне кажется, что каждый мой день похож на предыдущий. Ничего меня не радует. Ни от чего я не грущу.</Text>
                </Div>
                <Div className="howFeelThisEm">Как ощущается эта эмоция?</Div>
                <Div className="choiceEmState">
                    <Link onClick={ChangeColorBtn(1, state1P)}><Div className="stateEm1">Спокойствие</Div></Link>
                    <Link onClick={ChangeColorBtn(2, state2P)}><Div className="stateEm2">Апатия</Div></Link>
                    <Link onClick={ChangeColorBtn(3, state3P)}><Div className="stateEm3">Равнодушие</Div></Link>
                </Div>
                <Link><Div className="takePhoto">Сделать фото</Div></Link>
                <Link><Div className="saveWithoutPhoto">Сохранить без фото</Div></Link>
                <Div className="vetNiz"></Div>
            </Div>
        </Panel>
    );
}

export default myThingsPage;
